part_1 = inv_s1s2 + inv_s3 + inv_ver  #points earned in Part 1

ghg_s1_py = (V or 0) + (W or 0)  #gross PY S1 GHG emissions

ghg_s2_py = (X or 0) + (Y or 0)  #gross PY S2 GHG emissions

ghg_s1_by = (AQ or 0) + (AR or 0)  #gross BY S1 GHG emissions

ghg_s2_by = (AS or 0) + (AT or 0)  #gross BY S2 GHG emissions

ghg_py = (V or 0) + (W or 0) + (X or 0) + (Y or 0)  #gross PY GHG emissions

ghg_by = (AQ or 0) + (AR or 0) + (AS or 0) + (AT or 0)  #gross BY GHG emissions

s1_offsets_py = (AA or 0) + (AB or 0) + (H or 0) + (I or 0)  #PY S1 carbon offsets

s1_offsets_by = (B or 0) + (C or 0) + (Z or 0) + (AC or 0)  #BY S1 carbon offsets

ghg_s2_net_py = ghg_s2_py - (AD or 0)

if ghg_s2_net_py <= 0:
    ghg_s2_net_py_final = 0
else:
    ghg_s2_net_py_final = ghg_s2_net_py 

ghg_py_net = ghg_s1_py + ghg_s2_net_py_final - s1_offsets_py   #PY adjusted net GHG emissions

ghg_s2_net_by = ghg_s2_by - (AU or 0)

if ghg_s2_net_by <= 0:
    ghg_s2_net_by_final = 0
else:
    ghg_s2_net_by_final = ghg_s2_net_by

ghg_by_net = ghg_s1_by + ghg_s2_net_by_final - s1_offsets_by  #BY adjusted net GHG emissions

pop_pt_py = (AH - AE) + (AI - AF) - (AJ)  #PY part time campus users

wcu_py = (AE + AF + AG) + (0.75 * pop_pt_py)  #PY weighted campus users

pop_pt_by = (AY - AV) + (AZ - AW) - (BU)  #BY part time campus users

wcu_by = (AV + AW + AX) + (0.75 * pop_pt_by)  #BY weighted campus users

if ghg_py_net <= 0:
     ghg_perwcu_py = 0
else:  
     ghg_perwcu_py = ghg_py_net / wcu_py  #PY emissions per user

if ghg_by_net <= 0:
     ghg_perwcu_by = 0.0000001
else:  
     ghg_perwcu_by = ghg_by_net / wcu_by  #BY emissions per user

if ghg_py_net <= 0:
    part_2 = 4
else:
    part_2 = 4 * ((ghg_perwcu_by - ghg_perwcu_py) / ghg_perwcu_by)

if part_2 <= 0:
    part_2 = 0
if part_2 > 4:
    part_2 = 4

adj_space = AK + (AN or 0) + 2*(AL or 0) + 2*(AM or 0)  #EUI adjusted space

if ghg_py_net <= 0:
    ghg_peradj_space_py = 0
else:
    ghg_peradj_space_py = ghg_py_net / adj_space  #emissions per EUI adjusted space

if ghg_py_net <= 0:
    part_3 = 4
else:
    part_3 = 4 * ((0.02 - ghg_peradj_space_py) / 0.02)

if part_3 <= 0:
    part_3 = 0
if part_3 > 4:
    part_3 = 4

if not P: 
    points = 0
else:
    points = part_1 + part_2 + part_3

