fields.validation_rules:
if F > A:
  errors['F'] = "Oops! The number of students served by the first program should be less than or equal to the total number of students enrolled at the institution."

if F > 200000:
  warnings['F'] = "The number of students served by the first program is larger than expected."

if K > A:
  errors['K'] = "Oops! The number of students served by the second program should be less than or equal to the total number of students enrolled at the institution."

if K > 200000:
  warnings['K'] = "The number of students served by the second program is larger than expected."

if P > A:
  errors['P'] = "Oops! The number of students served by the third program should be less than or equal to the total number of students enrolled at the institution."

if P > 200000:
  warnings['P'] = "The number of students served by the third program is larger than expected."

if U > A:
  errors['U'] = "Oops! The number of students served by all other programs should be less than or equal to the total number of students enrolled at the institution."

if U > 200000:
  warnings['U'] = "The number of students served by all other programs is larger than expected."

if K:  # number of students for 2nd program is non-zero...
  if not G:
    errors['G'] = "This field is required if you wish to earn points for the students served by the second program."

  if not H:
    errors['H'] = "This field is required if you wish to earn points for the students served by the second program."

  if not I:
    errors['I'] = "This field is required if you wish to earn points for the students served by the second program."

  if not J:
    errors['J'] = "This field is required if you wish to earn points for the students served by the second program."


if P:   # number of students in 3rd program is non-zero
  if not L:
   errors['L'] = "This field is required if you wish to earn points for the students served by the third program."

  if not M:
    errors['M'] = "This field is required if you wish to earn points for the students served by the third program."

  if not N:
    errors['N'] = "This field is required if you wish to earn points for the students served by the third program."

  if not O:
    errors['O'] = "This field is required if you wish to earn points for the students served by the third program."

if U:  #number of students for all other programs is not zero
  if not Q:
    errors['Q'] = "This field is required if you wish to earn points for the students served by all other programs."

  if not R:
    errors['R'] = "This field is required if you wish to earn points for the students served by all other programs."

  if not S:
    errors['S'] = "This field is required if you wish to earn points for the students served by all other programs."

  if not T:
    errors['T'] = "This field is required if you wish to earn points for the students served by all other programs."

fields.formula:
var1 = F + (K or 0) + (P or 0) + (U or 0)

if var1 >= A:
  var2 = A 
else:
  var2 = var1


points = 5 * (var2/A)
